function chkadmin()
{
    if(document.getElementById('services')!=null )
    {
        if(document.getElementById('hiddenadmin').value=="yes")
        {
            document.getElementById('services').style.display="block";
            
        
        }
        else
        {
            document.getElementById('services').style.display="none";
        }
    }    
    
    var username = document.getElementById('hiddenusername').value;
	var compuser=document.getElementById('hiddencompuser').value;
	var admin=document.getElementById('hiddenadmin').value;
	var datetime=document.getElementById('hiddendatetime').value;
	document.getElementById("Topbar1__lbuser").innerHTML="User:";
    document.getElementById("Topbar1__lbcom").innerHTML="Organizational Unit:";
    document.getElementById("Topbar1__lbadmin").innerHTML="Profile:";
	document.getElementById("Topbar1__lbdatetime").innerHTML="Login Time:";
	if(compuser=="Agency" && admin=="yes")
	{
	    comp_="Agency";
	    admin_="Admin";
	}
	else if(admin=="yes" && compuser!="Agency")
	{
	    comp_=document.getElementById('hiddencentername').value;
	    admin_="Admin";
	}
	else if(compuser=="Agency" && admin!="yes")
	{
	    comp_="Agency";
	    admin_="User";
	
	}
	else if(compuser!="Agency" && admin!="yes")
	{
	    comp_=document.getElementById('hiddencenter').value;
	    admin_="User";
	
	}
	document.getElementById("Topbar1__user").innerHTML=username;
	document.getElementById("Topbar1__com").innerHTML=" "+comp_;
	document.getElementById("Topbar1__admin").innerHTML=admin_;
	document.getElementById("Topbar1__datetime").innerHTML=datetime;
    if(document.getElementById('hdndispauditbk').value=="Y" || document.getElementById('hdndispauditbk').value=="y")
    {
     OPEN_GRID();
    }

}

